def ip(first = False, *args):
    args = first and ((first,) + args) or ()
    return (".").join([str(i) for i in [i < len(args) and args[i] or random.randint(0,255) for i in range(4)]])

def ipv6(first = False, *args):
    args = first and ((first,) + args) or ()
    return (":").join([str('%x' % i) for i in [i < len(args) and args[i] or random.randint(0,65535) for i in range(4)]]) + '/64'

def tld(i = False):
    data = random.sample(TLD, i or 1)
    return data if i else data[0]
    
def hostname(i = False):
    data = random.sample(HOSTNAME, i or 1)
    return data if i else data[0]

def domain():
    return (".").join(hostname(random.randint(1,4)) + tld(1))

